type EventType string

const (
	AddBlacklistEvent      EventType = "AddBlacklist"
	RemoveBlacklistEvent   EventType = "RemoveBlacklist"
	DestroyBlackFundsEvent EventType = "DestroyBlackFunds"
	SubmitBlacklistEvent   EventType = "SubmitBlacklist"
)

type Event struct {
	Blockchain  Blockchain
	Date        time.Time
	Contract    Contract
	Address     string
	Tx          string
	BlockNumber uint64
	Type        EventType
	Amount      int64
}

type EventStorage interface {
	GetLastEventBlock(blockchain Blockchain) (uint64, error)
	GetLatestEvents(limit uint64) ([]*Event, error)
	GetLatestEventsFiltered(limit uint64, blockchain *Blockchain) ([]*Event, error)
	GetEventsByAddress(address string) ([]*Event, error)
	Store(event *Event) error
}

type Watcher interface {
	Watch(ctx context.Context) error
}
